mapper.update:
update `order`
user_id = #{userId}, goods_id = #{goodsId}, goods_name = #{goodsName}, goods_amount = #{goodsAmount}, payment = #{payment}, delivery_address = #{deliveryAddress}, delivery_phone = #{deliveryPhone}, delivery_receiver = #{deliveryReceiver}, user_remark = #{userRemark}, admin_remark = #{adminRemark}, status = #{status}, create_time = #{createTime}, pay_time = #{payTime}, sent_time = #{sentTime}, channel = #{channel}, pay_id = #{payId},
where id = #{id}

mapper.select:
SELECT * FROM goods


        AND goods_name LIKE CONCAT('%', #{keyword}, '%')


        AND user_id = #{userid}


ORDER BY id DESC LIMIT #{start}, #{pageSize}

SELECT count(*) FROM `order`


        AND goods_name LIKE CONCAT('%', #{keyword}, '%')


        AND user_id = #{userid}
