mapper.update:
update `order`
user_id = #{userId}, goods_id = #{goodsId}, goods_name = #{goodsName}, goods_amount = #{goodsAmount}, payment = #{payment}, delivery_address = #{deliveryAddress}, delivery_phone = #{deliveryPhone}, delivery_receiver = #{deliveryReceiver}, user_remark = #{userRemark}, admin_remark = #{adminRemark}, status = #{status}, create_time = #{createTime}, pay_time = #{payTime}, sent_time = #{sentTime}, channel = #{channel}, pay_id = #{payId}, seckill_event_id = #{seckillEventId},
where id = #{id}

mapper.select:
SELECT * FROM `order`


        AND user_id = #{userId}


        AND (id = #{searchId} OR user_id = #{searchId} OR goods_id = #{searchId})


        AND (goods_name LIKE CONCAT('%', #{searchName}, '%') OR delivery_receiver LIKE CONCAT('%', #{searchName}, '%'))


        AND status = #{selectStatus}


        AND channel = #{selectChannel}


ORDER BY id DESC LIMIT #{start}, #{pageSize}

SELECT count(*) FROM `order`


        AND user_id = #{userId}


        AND (id = #{searchId} OR user_id = #{searchId} OR goods_id = #{searchId})


        AND (goods_name LIKE CONCAT('%', #{searchName}, '%') OR delivery_receiver LIKE CONCAT('%', #{searchName}, '%'))


        AND status = #{selectStatus}


        AND channel = #{selectChannel}
