mapper.select:
SELECT count(*) FROM `seckill_event`


        title LIKE CONCAT('%', #{keyword}, '%')
        OR ID = #{keyword}

SELECT id,goods_id,title,seckill_price,seckill_stock,start_time,end_time,purchase_limit_num FROM seckill_event


        title LIKE CONCAT('%', #{keyword}, '%')
        OR ID = #{keyword}


ORDER BY id DESC LIMIT #{start}, #{pageSize}

mapper.update:
update seckill_event
title = #{title}, seckill_price = #{seckillPrice}, seckill_stock = #{seckillStock}, start_time = #{startTime}, end_time = #{endTime}, purchase_limit_num = #{purchaseLimitNum},
where id = #{id}
